import{_ as e,o,c,a as d}from"./app.e16bdd88.js";const g=JSON.parse('{"title":"\u5FAE\u524D\u7AEF\u662F\u4EC0\u4E48","description":"","frontmatter":{"sidebarDepth":2},"headers":[{"level":2,"title":"iframe \u65B9\u6848","slug":"iframe-\u65B9\u6848"},{"level":2,"title":"single-spa \u65B9\u6848","slug":"single-spa-\u65B9\u6848"},{"level":2,"title":"\u65E0\u754C\u65B9\u6848","slug":"\u65E0\u754C\u65B9\u6848"},{"level":3,"title":"\u5E94\u7528\u52A0\u8F7D\u673A\u5236\u548C js \u6C99\u7BB1\u673A\u5236","slug":"\u5E94\u7528\u52A0\u8F7D\u673A\u5236\u548C-js-\u6C99\u7BB1\u673A\u5236"},{"level":3,"title":"\u8DEF\u7531\u540C\u6B65\u673A\u5236","slug":"\u8DEF\u7531\u540C\u6B65\u673A\u5236"},{"level":3,"title":"iframe \u8FDE\u63A5\u673A\u5236\u548C css \u6C99\u7BB1\u673A\u5236","slug":"iframe-\u8FDE\u63A5\u673A\u5236\u548C-css-\u6C99\u7BB1\u673A\u5236"},{"level":3,"title":"\u901A\u4FE1\u673A\u5236","slug":"\u901A\u4FE1\u673A\u5236"},{"level":3,"title":"\u4F18\u52BF","slug":"\u4F18\u52BF"}],"relativePath":"guide/index.md","lastUpdated":1662016430000}'),i={name:"guide/index.md"},t=d('<h1 id="\u5FAE\u524D\u7AEF\u662F\u4EC0\u4E48" tabindex="-1">\u5FAE\u524D\u7AEF\u662F\u4EC0\u4E48 <a class="header-anchor" href="#\u5FAE\u524D\u7AEF\u662F\u4EC0\u4E48" aria-hidden="true">#</a></h1><p>\u5FAE\u524D\u7AEF\u662F\u4E00\u79CD\u591A\u4E2A\u56E2\u961F\u901A\u8FC7\u72EC\u7ACB\u53D1\u5E03\u529F\u80FD\u7684\u65B9\u5F0F\u6765\u5171\u540C\u6784\u5EFA\u73B0\u4EE3\u5316 web \u5E94\u7528\u7684\u6280\u672F\u624B\u6BB5\u53CA\u65B9\u6CD5\u7B56\u7565\u3002</p><div class="tip custom-block"><p class="custom-block-title">\u5F15\u7528</p><p>Techniques, strategies and recipes for building a modern web app with multiple teams that can ship features independently. -- <a href="https://micro-frontends.org/" target="_blank" rel="noreferrer">Micro Frontends</a></p></div><p>\u901A\u4FD7\u6765\u8BF4\uFF0C\u5C31\u662F\u5728\u4E00\u4E2A<code>web</code>\u5E94\u7528\u4E2D\u53EF\u4EE5\u72EC\u7ACB\u7684\u8FD0\u884C\u53E6\u4E00\u4E2A<code>web</code>\u5E94\u7528</p><p>\u5FAE\u524D\u7AEF\u6709\u4EC0\u4E48\u4F7F\u7528\u573A\u666F\u5462\uFF1F</p><div class="tip custom-block"><p class="custom-block-title">\u4E3E\u4F8B</p><ul><li><p>\u6BD4\u5982\u5236\u4F5C\u4E00\u4E2A\u4F01\u4E1A\u7BA1\u7406\u5E73\u53F0\uFF0C\u628A\u5DF2\u6709\u7684\u91C7\u8D2D\u7CFB\u7EDF\u548C\u8D22\u52A1\u7CFB\u7EDF\u7EDF\u4E00\u63A5\u5165\u8FD9\u4E2A\u5E73\u53F0\uFF1B</p></li><li><p>\u6BD4\u5982\u6709\u4E00\u4E2A\u5DE8\u5927\u7684\u5E94\u7528\uFF0C\u4E3A\u4E86\u964D\u4F4E\u5F00\u53D1\u548C\u7EF4\u62A4\u6210\u672C\uFF0C\u5206\u62C6\u6210\u591A\u4E2A\u5C0F\u5E94\u7528\u8FDB\u884C\u5F00\u53D1\u548C\u90E8\u7F72\uFF0C\u7136\u540E\u7528\u4E00\u4E2A\u5E73\u53F0\u5C06\u8FD9\u4E9B\u5C0F\u5E94\u7528\u96C6\u6210\u8D77\u6765\uFF1B</p></li><li><p>\u53C8\u6BD4\u5982\u4E00\u4E2A\u5E94\u7528\u4F7F\u7528<code>vue</code>\u6846\u67B6\u5F00\u53D1\uFF0C\u5176\u4E2D\u6709\u4E00\u4E2A\u6BD4\u8F83\u72EC\u7ACB\u7684\u6A21\u5757\uFF0C\u5F00\u53D1\u8005\u60F3\u5C1D\u8BD5\u4F7F\u7528<code>react</code>\u6846\u67B6\u6765\u5F00\u53D1\uFF0C\u7B49\u6A21\u5757\u5355\u72EC\u5F00\u53D1\u90E8\u7F72\u5B8C\uFF0C\u518D\u628A\u8FD9\u4E2A\u6A21\u5757\u5E94\u7528\u63A5\u56DE\u53BB</p></li></ul></div><p>\u4E00\u4E2A\u5B8C\u5584\u7684\u7684\u5FAE\u524D\u7AEF\u6846\u67B6\u5E94\u8BE5\u5177\u5907\u54EA\u4E9B\u80FD\u529B\u5462\uFF1F</p><div class="tip custom-block"><p class="custom-block-title">\u80FD\u529B</p><ul><li><p><strong>\u5B50\u5E94\u7528\u7684\u52A0\u8F7D\u548C\u5378\u8F7D\u80FD\u529B</strong></p><p>\u9875\u9762\u9700\u8981\u4ECE\u4E00\u4E2A\u5B50\u5E94\u7528\u5207\u6362\u5230\u53E6\u4E00\u4E2A\u5B50\u5E94\u7528\uFF0C\u6846\u67B6\u5FC5\u987B\u5177\u5907\u52A0\u8F7D\u3001\u6E32\u67D3\u3001\u5207\u6362\u7684\u80FD\u529B</p></li><li><p><strong>\u5B50\u5E94\u7528\u72EC\u7ACB\u8FD0\u884C\u7684\u80FD\u529B</strong></p><p>\u5B50\u5E94\u7528\u8FD0\u884C\u4F1A\u6C61\u67D3\u5168\u5C40\u7684 window \u5BF9\u8C61\uFF0C\u6837\u5F0F\u4F1A\u6C61\u67D3\u5176\u4ED6\u5E94\u7528\uFF0C\u5FC5\u987B\u6709\u6548\u7684\u9694\u79BB\u8D77\u6765</p></li><li><p><strong>\u5B50\u5E94\u7528\u8DEF\u7531\u72B6\u6001\u4FDD\u6301\u80FD\u529B</strong></p><p>\u6FC0\u6D3B\u5B50\u5E94\u7528\u540E\uFF0C\u6D4F\u89C8\u5668\u5237\u65B0\u3001\u524D\u8FDB\u3001\u540E\u9000\u5B50\u5E94\u7528\u7684\u8DEF\u7531\u90FD\u5E94\u8BE5\u53EF\u4EE5\u6B63\u5E38\u5DE5\u4F5C</p></li><li><p><strong>\u5E94\u7528\u95F4\u901A\u4FE1\u7684\u80FD\u529B</strong></p><p>\u5E94\u7528\u95F4\u53EF\u4EE5\u65B9\u4FBF\u3001\u5FEB\u6377\u7684\u901A\u4FE1</p></li></ul></div><p>\u4F7F\u7528\u5FAE\u524D\u7AEF\u6709\u4EC0\u4E48\u6536\u76CA\u5462\uFF1F(<a href="https://qiankun.umijs.org/zh/guide" target="_blank" rel="noreferrer">@reference \u4E7E\u5764</a>)</p><div class="tip custom-block"><p class="custom-block-title">\u6536\u76CA</p><ul><li><p><strong>\u6280\u672F\u6808\u65E0\u5173</strong></p><p>\u4E3B\u6846\u67B6\u4E0D\u9650\u5236\u63A5\u5165\u5E94\u7528\u7684\u6280\u672F\u6808\uFF0C\u5FAE\u5E94\u7528\u5177\u5907\u5B8C\u5168\u81EA\u4E3B\u6743</p></li><li><p><strong>\u72EC\u7ACB\u5F00\u53D1\u3001\u72EC\u7ACB\u90E8\u7F72</strong></p><p>\u5FAE\u5E94\u7528\u4ED3\u5E93\u72EC\u7ACB\uFF0C\u524D\u540E\u7AEF\u53EF\u72EC\u7ACB\u5F00\u53D1\uFF0C\u90E8\u7F72\u5B8C\u6210\u540E\u4E3B\u6846\u67B6\u81EA\u52A8\u5B8C\u6210\u540C\u6B65\u66F4\u65B0</p></li><li><p><strong>\u589E\u91CF\u5347\u7EA7</strong></p><p>\u5728\u9762\u5BF9\u5404\u79CD\u590D\u6742\u573A\u666F\u65F6\uFF0C\u6211\u4EEC\u901A\u5E38\u5F88\u96BE\u5BF9\u4E00\u4E2A\u5DF2\u7ECF\u5B58\u5728\u7684\u7CFB\u7EDF\u505A\u5168\u91CF\u7684\u6280\u672F\u6808\u5347\u7EA7\u6216\u91CD\u6784\uFF0C\u800C\u5FAE\u524D\u7AEF\u662F\u4E00\u79CD\u975E\u5E38\u597D\u7684\u5B9E\u65BD\u6E10\u8FDB\u5F0F\u91CD\u6784\u7684\u624B\u6BB5\u548C\u7B56\u7565</p></li><li><p><strong>\u72EC\u7ACB\u8FD0\u884C\u65F6</strong></p><p>\u6BCF\u4E2A\u5FAE\u5E94\u7528\u4E4B\u95F4\u72B6\u6001\u9694\u79BB\uFF0C\u8FD0\u884C\u65F6\u72B6\u6001\u4E0D\u5171\u4EAB</p></li></ul></div><p>\u53EF\u80FD\u6709\u4EBA\u4F1A\u6709\u7591\u95EE\u76F4\u63A5\u4F7F\u7528<code>iframe</code>\u4E0D\u5C31\u53EF\u4EE5\u505A\u5230\u5417\uFF1F</p><h2 id="iframe-\u65B9\u6848" tabindex="-1">iframe \u65B9\u6848 <a class="header-anchor" href="#iframe-\u65B9\u6848" aria-hidden="true">#</a></h2><p>\u91C7\u7528<code>iframe</code>\u7684\u65B9\u6848\u786E\u5B9E\u53EF\u4EE5\u505A\u5230\uFF0C\u800C\u4E14\u4F18\u70B9\u975E\u5E38\u660E\u663E</p><div class="tip custom-block"><p class="custom-block-title">\u4F18\u70B9</p><ul><li>\u975E\u5E38\u7B80\u5355\uFF0C\u4F7F\u7528\u6CA1\u6709\u4EFB\u4F55\u5FC3\u667A\u8D1F\u62C5</li><li><code>web</code>\u5E94\u7528\u9694\u79BB\u7684\u975E\u5E38\u5B8C\u7F8E\uFF0C\u65E0\u8BBA\u662F<code>js</code>\u3001<code>css</code>\u3001<code>dom</code>\u90FD\u5B8C\u5168\u9694\u79BB\u5F00\u6765</li></ul></div><p>\u7531\u4E8E\u5176<strong>\u9694\u79BB\u7684\u592A\u5B8C\u7F8E</strong>\u5BFC\u81F4\u7F3A\u70B9\u4E5F\u975E\u5E38\u660E\u663E</p><div class="danger custom-block"><p class="custom-block-title">\u7F3A\u70B9</p><ul><li>\u8DEF\u7531\u72B6\u6001\u4E22\u5931\uFF0C\u5237\u65B0\u4E00\u4E0B\uFF0C<code>iframe</code>\u7684<code>url</code>\u72B6\u6001\u5C31\u4E22\u5931\u4E86</li><li><code>dom</code>\u5272\u88C2\u4E25\u91CD\uFF0C\u5F39\u7A97\u53EA\u80FD\u5728<code>iframe</code>\u5185\u90E8\u5C55\u793A\uFF0C\u65E0\u6CD5\u8986\u76D6\u5168\u5C40</li><li><code>web</code>\u5E94\u7528\u4E4B\u95F4\u901A\u4FE1\u975E\u5E38\u56F0\u96BE</li><li>\u6BCF\u6B21\u6253\u5F00\u767D\u5C4F\u65F6\u95F4\u592A\u957F\uFF0C\u5BF9\u4E8E<a href="https://zh.wikipedia.org/wiki/%E5%8D%95%E9%A1%B5%E5%BA%94%E7%94%A8" target="_blank" rel="noreferrer">SPA \u5E94\u7528</a>\u6765\u8BF4\u65E0\u6CD5\u63A5\u53D7</li></ul></div><h2 id="single-spa-\u65B9\u6848" tabindex="-1">single-spa \u65B9\u6848 <a class="header-anchor" href="#single-spa-\u65B9\u6848" aria-hidden="true">#</a></h2><p><a href="https://zh-hans.single-spa.js.org/docs/getting-started-overview" target="_blank" rel="noreferrer">single-spa</a>\u662F\u4E00\u4E2A\u76EE\u524D\u4E3B\u6D41\u7684\u5FAE\u524D\u7AEF\u6280\u672F\u65B9\u6848\uFF0C\u5176\u4E3B\u8981\u5B9E\u73B0\u601D\u8DEF\uFF1A</p><ul><li>\u9884\u5148\u6CE8\u518C\u5B50\u5E94\u7528(\u6FC0\u6D3B\u8DEF\u7531\u3001\u5B50\u5E94\u7528\u8D44\u6E90\u3001\u751F\u547D\u5468\u671F\u51FD\u6570)</li><li>\u76D1\u542C\u8DEF\u7531\u7684\u53D8\u5316\uFF0C\u5339\u914D\u5230\u4E86\u6FC0\u6D3B\u7684\u8DEF\u7531\u5219\u52A0\u8F7D\u5B50\u5E94\u7528\u8D44\u6E90\uFF0C\u987A\u5E8F\u8C03\u7528\u751F\u547D\u5468\u671F\u51FD\u6570\u5E76\u6700\u7EC8\u6E32\u67D3\u5230\u5BB9\u5668</li></ul><p><a href="https://qiankun.umijs.org/zh/guide" target="_blank" rel="noreferrer">\u4E7E\u5764</a>\u5FAE\u524D\u7AEF\u67B6\u6784\u5219\u8FDB\u4E00\u6B65\u5BF9<code>single-spa</code>\u65B9\u6848\u8FDB\u884C\u5B8C\u5584\uFF0C\u4E3B\u8981\u7684\u5B8C\u5584\u70B9\uFF1A</p><ul><li>\u5B50\u5E94\u7528\u8D44\u6E90\u7531 js \u5217\u8868\u4FEE\u6539\u8FDB\u4E3A\u4E00\u4E2A<code>url</code>\uFF0C\u5927\u5927\u51CF\u8F7B\u6CE8\u518C\u5B50\u5E94\u7528\u7684\u590D\u6742\u5EA6</li><li>\u5B9E\u73B0\u5E94\u7528\u9694\u79BB\uFF0C\u5B8C\u6210<code>js</code>\u9694\u79BB\u65B9\u6848 <em>\uFF08<code>window</code>\u5DE5\u5382\uFF09</em> \u548C<code>css</code>\u9694\u79BB\u65B9\u6848 <em>\uFF08\u7C7B<code>vue</code>\u7684<code>scoped</code>\uFF09</em></li><li>\u589E\u52A0\u8D44\u6E90\u9884\u52A0\u8F7D\u80FD\u529B\uFF0C\u9884\u5148\u5B50\u5E94\u7528<code>html</code>\u3001<code>js</code>\u3001<code>css</code>\u8D44\u6E90\u7F13\u5B58\u4E0B\u6765\uFF0C\u52A0\u5FEB\u5B50\u5E94\u7528\u7684\u6253\u5F00\u901F\u5EA6</li></ul><p>\u603B\u7ED3\u4E00\u4E0B\u65B9\u6848\u7684\u4F18\u7F3A\u70B9\uFF1A</p><div class="tip custom-block"><p class="custom-block-title">\u4F18\u70B9</p><ul><li>\u76D1\u542C\u8DEF\u7531\u81EA\u52A8\u7684\u52A0\u8F7D\u3001\u5378\u8F7D\u5F53\u524D\u8DEF\u7531\u5BF9\u5E94\u7684\u5B50\u5E94\u7528</li><li>\u5B8C\u5907\u7684\u6C99\u7BB1\u65B9\u6848\uFF0C<code>js</code>\u6C99\u7BB1\u505A\u4E86<code>SnapshotSandbox</code>\u3001<code>LegacySandbox</code>\u3001<code>ProxySandbox</code>\u4E09\u5957\u6E10\u8FDB\u589E\u5F3A\u65B9\u6848\uFF0C<code>css</code>\u6C99\u7BB1\u505A\u4E86\u4E24\u5957<code>strictStyleIsolation</code>\u3001<code>experimentalStyleIsolation</code>\u4E24\u5957\u9002\u7528\u4E0D\u540C\u573A\u666F\u7684\u65B9\u6848</li><li>\u8DEF\u7531\u4FDD\u6301\uFF0C\u6D4F\u89C8\u5668\u5237\u65B0\u3001\u524D\u8FDB\u3001\u540E\u9000\uFF0C\u90FD\u53EF\u4EE5\u4F5C\u7528\u5230\u5B50\u5E94\u7528</li><li>\u5E94\u7528\u95F4\u901A\u4FE1\u7B80\u5355\uFF0C\u5168\u5C40\u6CE8\u5165</li></ul></div><div class="danger custom-block"><p class="custom-block-title">\u7F3A\u70B9</p><ul><li>\u57FA\u4E8E\u8DEF\u7531\u5339\u914D\uFF0C\u65E0\u6CD5\u540C\u65F6\u6FC0\u6D3B\u591A\u4E2A\u5B50\u5E94\u7528\uFF0C\u4E5F\u4E0D\u652F\u6301\u5B50\u5E94\u7528\u4FDD\u6D3B</li><li>\u6539\u9020\u6210\u672C\u8F83\u5927\uFF0C\u4ECE <code>webpack</code>\u3001\u4EE3\u7801\u3001\u8DEF\u7531\u7B49\u7B49\u90FD\u8981\u505A\u4E00\u7CFB\u5217\u7684\u9002\u914D</li><li><code>css</code> \u6C99\u7BB1\u65E0\u6CD5\u7EDD\u5BF9\u7684\u9694\u79BB\uFF0C<code>js</code> \u6C99\u7BB1\u5728\u67D0\u4E9B\u573A\u666F\u4E0B\u6267\u884C\u6027\u80FD\u4E0B\u964D\u4E25\u91CD</li><li>\u65E0\u6CD5\u652F\u6301 <code>vite</code> \u7B49 <code>ESM</code> \u811A\u672C\u8FD0\u884C</li></ul></div><h2 id="\u65E0\u754C\u65B9\u6848" tabindex="-1">\u65E0\u754C\u65B9\u6848 <a class="header-anchor" href="#\u65E0\u754C\u65B9\u6848" aria-hidden="true">#</a></h2><p>\u5728\u4E7E\u5764\u7684<code>issue</code>\u4E2D\u4E00\u4E2A<a href="https://github.com/umijs/qiankun/issues/286" target="_blank" rel="noreferrer">\u8BAE\u9898</a>\u975E\u5E38\u6709\u610F\u601D\uFF0C\u6709\u4E2A\u5F00\u53D1\u8005\u63D0\u51FA\u80FD\u5426\u5229\u7528<code>iframe</code>\u6765\u5B9E\u73B0<code>js</code>\u6C99\u7BB1\u80FD\u529B\uFF0C\u8FD9\u4E2A<code>idea</code>\u542F\u53D1\u4E86\u65E0\u754C\u65B9\u6848\uFF0C\u4E0B\u9762\u8BE6\u7EC6\u4ECB\u7ECD</p><h3 id="\u5E94\u7528\u52A0\u8F7D\u673A\u5236\u548C-js-\u6C99\u7BB1\u673A\u5236" tabindex="-1">\u5E94\u7528\u52A0\u8F7D\u673A\u5236\u548C js \u6C99\u7BB1\u673A\u5236 <a class="header-anchor" href="#\u5E94\u7528\u52A0\u8F7D\u673A\u5236\u548C-js-\u6C99\u7BB1\u673A\u5236" aria-hidden="true">#</a></h3><p>\u5C06\u5B50\u5E94\u7528\u7684<code>js</code>\u6CE8\u5165\u4E3B\u5E94\u7528\u540C\u57DF\u7684<code>iframe</code>\u4E2D\u8FD0\u884C\uFF0C<code>iframe</code>\u662F\u4E00\u4E2A\u539F\u751F\u7684<code>window</code>\u6C99\u7BB1\uFF0C\u5185\u90E8\u6709\u5B8C\u6574\u7684<code>history</code>\u548C<code>location</code>\u63A5\u53E3\uFF0C\u5B50\u5E94\u7528\u5B9E\u4F8B<code>instance</code>\u8FD0\u884C\u5728<code>iframe</code>\u4E2D\uFF0C\u8DEF\u7531\u4E5F\u5F7B\u5E95\u548C\u4E3B\u5E94\u7528\u89E3\u8026\uFF0C\u53EF\u4EE5\u76F4\u63A5\u5728\u4E1A\u52A1\u7EC4\u4EF6\u91CC\u9762\u542F\u52A8\u5E94\u7528\u3002</p><p>\u91C7\u7528\u8FD9\u79CD\u65B9\u5F0F\u6211\u4EEC\u53EF\u4EE5\u83B7\u5F97</p><div class="tip custom-block"><p class="custom-block-title">\u6536\u76CA</p><ul><li><p><strong>\u7EC4\u4EF6\u65B9\u5F0F\u6765\u4F7F\u7528\u5FAE\u524D\u7AEF</strong></p><p>\u4E0D\u7528\u6CE8\u518C\uFF0C\u4E0D\u7528\u6539\u9020\u8DEF\u7531\uFF0C\u76F4\u63A5\u4F7F\u7528\u65E0\u754C\u7EC4\u4EF6\uFF0C\u5316\u7E41\u4E3A\u7B80</p></li><li><p><strong>\u4E00\u4E2A\u9875\u9762\u53EF\u4EE5\u540C\u65F6\u6FC0\u6D3B\u591A\u4E2A\u5B50\u5E94\u7528</strong></p><p>\u5B50\u5E94\u7528\u91C7\u7528 iframe \u7684\u8DEF\u7531\uFF0C\u4E0D\u7528\u5173\u5FC3\u8DEF\u7531\u5360\u7528\u7684\u95EE\u9898</p></li><li><p><strong>\u5929\u7136 js \u6C99\u7BB1\uFF0C\u4E0D\u4F1A\u6C61\u67D3\u4E3B\u5E94\u7528\u73AF\u5883</strong></p><p>\u4E0D\u7528\u4FEE\u6539\u4E3B\u5E94\u7528<code>window</code>\u4EFB\u4F55\u5C5E\u6027\uFF0C\u53EA\u5728<code>iframe</code>\u5185\u90E8\u8FDB\u884C\u4FEE\u6539</p></li><li><p><strong>\u5E94\u7528\u5207\u6362\u6CA1\u6709\u6E05\u7406\u6210\u672C</strong></p><p>\u7531\u4E8E\u4E0D\u6C61\u67D3\u4E3B\u5E94\u7528\uFF0C\u5B50\u5E94\u7528\u9500\u6BC1\u4E5F\u65E0\u9700\u505A\u4EFB\u4F55\u6E05\u7406\u5DE5\u4F5C</p></li></ul></div><h3 id="\u8DEF\u7531\u540C\u6B65\u673A\u5236" tabindex="-1">\u8DEF\u7531\u540C\u6B65\u673A\u5236 <a class="header-anchor" href="#\u8DEF\u7531\u540C\u6B65\u673A\u5236" aria-hidden="true">#</a></h3><p>\u5728<code>iframe</code>\u5185\u90E8\u8FDB\u884C<code>history.pushState</code>\uFF0C\u6D4F\u89C8\u5668\u4F1A\u81EA\u52A8\u7684\u5728<a href="https://html.spec.whatwg.org/multipage/history.html#joint-session-history" target="_blank" rel="noreferrer">joint session history</a>\u4E2D\u6DFB\u52A0<code>iframe</code>\u7684<a href="https://html.spec.whatwg.org/multipage/history.html#session-history" target="_blank" rel="noreferrer">session-history</a>\uFF0C\u6D4F\u89C8\u5668\u7684\u524D\u8FDB\u3001\u540E\u9000\u5728\u4E0D\u505A\u4EFB\u4F55\u5904\u7406\u7684\u60C5\u51B5\u5C31\u53EF\u4EE5\u76F4\u63A5\u4F5C\u7528\u4E8E\u5B50\u5E94\u7528</p><p>\u52AB\u6301<code>iframe</code>\u7684<code>history.pushState</code>\u548C<code>history.replaceState</code>\uFF0C\u5C31\u53EF\u4EE5\u5C06\u5B50\u5E94\u7528\u7684<code>url</code>\u540C\u6B65\u5230\u4E3B\u5E94\u7528\u7684<code>query</code>\u53C2\u6570\u4E0A\uFF0C\u5F53\u5237\u65B0\u6D4F\u89C8\u5668\u521D\u59CB\u5316<code>iframe</code>\u65F6\uFF0C\u8BFB\u56DE\u5B50\u5E94\u7528\u7684<code>url</code>\u5E76\u4F7F\u7528<code>iframe</code>\u7684<code>history.replaceState</code>\u8FDB\u884C\u540C\u6B65</p><p>\u91C7\u7528\u8FD9\u79CD\u65B9\u5F0F\u6211\u4EEC\u53EF\u4EE5\u83B7\u5F97</p><div class="tip custom-block"><p class="custom-block-title">\u6536\u76CA</p><ul><li><strong>\u6D4F\u89C8\u5668\u5237\u65B0\u3001\u524D\u8FDB\u3001\u540E\u9000\u90FD\u53EF\u4EE5\u4F5C\u7528\u5230\u5B50\u5E94\u7528</strong></li><li><strong>\u5B9E\u73B0\u6210\u672C\u4F4E\uFF0C\u65E0\u9700\u590D\u6742\u7684\u76D1\u542C\u6765\u5904\u7406\u540C\u6B65\u95EE\u9898</strong></li><li><strong>\u591A\u5E94\u7528\u540C\u65F6\u6FC0\u6D3B\u65F6\u4E5F\u80FD\u4FDD\u6301\u8DEF\u7531\u540C\u6B65</strong></li></ul></div><h3 id="iframe-\u8FDE\u63A5\u673A\u5236\u548C-css-\u6C99\u7BB1\u673A\u5236" tabindex="-1">iframe \u8FDE\u63A5\u673A\u5236\u548C css \u6C99\u7BB1\u673A\u5236 <a class="header-anchor" href="#iframe-\u8FDE\u63A5\u673A\u5236\u548C-css-\u6C99\u7BB1\u673A\u5236" aria-hidden="true">#</a></h3><p>\u65E0\u754C\u91C7\u7528<a href="https://developer.mozilla.org/en-US/docs/Web/Web_Components" target="_blank" rel="noreferrer">webcomponent</a>\u6765\u5B9E\u73B0\u9875\u9762\u7684\u6837\u5F0F\u9694\u79BB\uFF0C\u65E0\u754C\u4F1A\u521B\u5EFA\u4E00\u4E2A<code>wujie</code>\u81EA\u5B9A\u4E49\u5143\u7D20\uFF0C\u7136\u540E\u5C06\u5B50\u5E94\u7528\u7684\u5B8C\u6574\u7ED3\u6784\u6E32\u67D3\u5728\u5185\u90E8</p><p>\u5B50\u5E94\u7528\u7684\u5B9E\u4F8B<code>instance</code>\u5728<code>iframe</code>\u5185\u8FD0\u884C\uFF0C<code>dom</code>\u5728\u4E3B\u5E94\u7528\u5BB9\u5668\u4E0B\u7684<code>webcomponent</code>\u5185\uFF0C\u901A\u8FC7\u4EE3\u7406 <code>iframe</code>\u7684<code>document</code>\u5230<code>webcomponent</code>\uFF0C\u53EF\u4EE5\u5B9E\u73B0\u4E24\u8005\u7684\u4E92\u8054\u3002</p><p>\u5C06<code>document</code>\u7684\u67E5\u8BE2\u7C7B\u63A5\u53E3\uFF1A<code>getElementsByTagName\u3001getElementsByClassName\u3001getElementsByName\u3001getElementById\u3001querySelector\u3001querySelectorAll\u3001head\u3001body</code>\u5168\u90E8\u4EE3\u7406\u5230<code>webcomponent</code>\uFF0C\u8FD9\u6837<code>instance</code>\u548C<code>webcomponent</code>\u5C31\u7CBE\u51C6\u7684\u94FE\u63A5\u8D77\u6765\u3002</p><p>\u5F53\u5B50\u5E94\u7528\u53D1\u751F\u5207\u6362\uFF0C<code>iframe</code>\u4FDD\u7559\u4E0B\u6765\uFF0C\u5B50\u5E94\u7528\u7684\u5BB9\u5668\u53EF\u80FD\u9500\u6BC1\uFF0C\u4F46<code>webcomponent</code>\u4F9D\u7136\u53EF\u4EE5\u9009\u62E9\u4FDD\u7559\uFF0C\u8FD9\u6837\u7B49\u5E94\u7528\u5207\u6362\u56DE\u6765\u5C06<code>webcomponent</code>\u518D\u6302\u8F7D\u56DE\u5BB9\u5668\u4E0A\uFF0C\u5B50\u5E94\u7528\u53EF\u4EE5\u83B7\u5F97\u7C7B\u4F3C<code>vue</code>\u7684<code>keep-alive</code>\u7684\u80FD\u529B.</p><p>\u91C7\u7528\u8FD9\u79CD\u65B9\u5F0F\u6211\u4EEC\u53EF\u4EE5\u83B7\u5F97</p><div class="tip custom-block"><p class="custom-block-title">\u6536\u76CA</p><ul><li><p><strong>\u5929\u7136 css \u6C99\u7BB1</strong></p><p>\u76F4\u63A5\u7269\u7406\u9694\u79BB\uFF0C\u6837\u5F0F\u9694\u79BB\u5B50\u5E94\u7528\u4E0D\u7528\u505A\u4EFB\u4F55\u4FEE\u6539</p></li><li><p><strong>\u5929\u7136\u9002\u914D\u5F39\u7A97\u95EE\u9898</strong></p><p><code>document.body</code>\u7684<code>appendChild</code>\u6216\u8005<code>insertBefore</code>\u4F1A\u4EE3\u7406\u76F4\u63A5\u63D2\u5165\u5230<code>webcomponent</code>\uFF0C\u5B50\u5E94\u7528\u4E0D\u7528\u505A\u4EFB\u4F55\u6539\u9020</p></li><li><p><strong>\u5B50\u5E94\u7528\u4FDD\u6D3B</strong></p><p>\u5B50\u5E94\u7528\u4FDD\u7559<code>iframe</code>\u548C<code>webcomponent</code>\uFF0C\u5E94\u7528\u5185\u90E8\u7684<code>state</code>\u4E0D\u4F1A\u4E22\u5931</p></li><li><p><strong>\u5B8C\u6574\u7684 DOM \u7ED3\u6784</strong></p><p><code>webcomponent</code>\u4FDD\u7559\u4E86\u5B50\u5E94\u7528\u5B8C\u6574\u7684<code>html</code>\u7ED3\u6784\uFF0C\u6837\u5F0F\u548C\u7ED3\u6784\u5B8C\u5168\u5BF9\u5E94\uFF0C\u5B50\u5E94\u7528\u4E0D\u7528\u505A\u4EFB\u4F55\u4FEE\u6539</p></li></ul></div><h3 id="\u901A\u4FE1\u673A\u5236" tabindex="-1">\u901A\u4FE1\u673A\u5236 <a class="header-anchor" href="#\u901A\u4FE1\u673A\u5236" aria-hidden="true">#</a></h3><p>\u627F\u8F7D\u5B50\u5E94\u7528\u7684<code>iframe</code>\u548C\u4E3B\u5E94\u7528\u662F\u540C\u57DF\u7684\uFF0C\u6240\u4EE5\u4E3B\u3001\u5B50\u5E94\u7528\u5929\u7136\u5C31\u53EF\u4EE5\u5F88\u597D\u7684\u8FDB\u884C\u901A\u4FE1\uFF0C\u5728\u65E0\u754C\u6211\u4EEC\u63D0\u4F9B\u4E09\u79CD\u901A\u4FE1\u65B9\u5F0F</p><ul><li><strong>props \u6CE8\u5165\u673A\u5236</strong></li></ul><p>\u5B50\u5E94\u7528\u901A\u8FC7<code>$wujie.props</code>\u53EF\u4EE5\u8F7B\u677E\u62FF\u5230\u4E3B\u5E94\u7528\u6CE8\u5165\u7684\u6570\u636E</p><ul><li><strong>window.parent \u901A\u4FE1\u673A\u5236</strong></li></ul><p>\u5B50\u5E94\u7528<code>iframe</code>\u6C99\u7BB1\u548C\u4E3B\u5E94\u7528\u540C\u6E90\uFF0C\u5B50\u5E94\u7528\u53EF\u4EE5\u76F4\u63A5\u901A\u8FC7<code>window.parent</code>\u548C\u4E3B\u5E94\u7528\u901A\u4FE1</p><ul><li><strong>\u53BB\u4E2D\u5FC3\u5316\u7684\u901A\u4FE1\u673A\u5236</strong></li></ul><p>\u65E0\u754C\u63D0\u4F9B\u4E86<code>EventBus</code>\u5B9E\u4F8B\uFF0C\u6CE8\u5165\u5230\u4E3B\u5E94\u7528\u548C\u5B50\u5E94\u7528\uFF0C\u6240\u6709\u7684\u5E94\u7528\u53EF\u4EE5\u53BB\u4E2D\u5FC3\u5316\u7684\u8FDB\u884C\u901A\u4FE1</p><h3 id="\u4F18\u52BF" tabindex="-1">\u4F18\u52BF <a class="header-anchor" href="#\u4F18\u52BF" aria-hidden="true">#</a></h3><p>\u901A\u8FC7\u4E0A\u9762\u539F\u7406\u7684\u9610\u8FF0\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5F97\u51FA\u65E0\u754C\u5FAE\u524D\u7AEF\u6846\u67B6\u7684\u51E0\u70B9\u4F18\u52BF\uFF1A</p><div class="tip custom-block"><p class="custom-block-title">\u4F18\u52BF</p><ul><li><p><strong>\u591A\u5E94\u7528\u540C\u65F6\u6FC0\u6D3B\u5728\u7EBF</strong></p><p>\u6846\u67B6\u5177\u5907\u540C\u65F6\u6FC0\u6D3B\u591A\u5E94\u7528\uFF0C\u5E76\u4FDD\u6301\u8FD9\u4E9B\u5E94\u7528\u8DEF\u7531\u540C\u6B65\u7684\u80FD\u529B</p></li><li><p><strong>\u7EC4\u4EF6\u5F0F\u7684\u4F7F\u7528\u65B9\u5F0F</strong></p><p>\u65E0\u9700\u6CE8\u518C\uFF0C\u66F4\u65E0\u9700\u8DEF\u7531\u9002\u914D\uFF0C\u5728\u7EC4\u4EF6\u5185\u4F7F\u7528\uFF0C\u8DDF\u968F\u7EC4\u4EF6\u88C5\u8F7D\u3001\u5378\u8F7D</p></li><li><p><strong>\u5E94\u7528\u7EA7\u522B\u7684 keep-alive</strong></p><p>\u5B50\u5E94\u7528\u5F00\u542F<a href="/doc/api/startApp.html#alive">\u4FDD\u6D3B\u6A21\u5F0F</a>\u540E\uFF0C\u5E94\u7528\u53D1\u751F\u5207\u6362\u65F6\u6574\u4E2A\u5B50\u5E94\u7528\u7684\u72B6\u6001\u53EF\u4EE5\u4FDD\u5B58\u4E0B\u6765\u4E0D\u4E22\u5931\uFF0C\u7ED3\u5408<a href="/doc/api/preloadApp.html#exec">\u9884\u6267\u884C\u6A21\u5F0F</a>\u53EF\u4EE5\u83B7\u5F97\u7C7B\u4F3C<code>ssr</code>\u7684\u6253\u5F00\u4F53\u9A8C</p></li><li><p><strong>\u7EAF\u51C0\u65E0\u6C61\u67D3</strong></p><ul><li>\u65E0\u754C\u5229\u7528<code>iframe</code>\u548C<code>webcomponent</code>\u6765\u642D\u5EFA\u5929\u7136\u7684<code>js</code>\u9694\u79BB\u6C99\u7BB1\u548C<code>css</code>\u9694\u79BB\u6C99\u7BB1</li><li>\u5229\u7528<code>iframe</code>\u7684<code>history</code>\u548C\u4E3B\u5E94\u7528\u7684<code>history</code>\u5728\u540C\u4E00\u4E2A<a href="https://html.spec.whatwg.org/multipage/browsers.html#top-level-browsing-context" target="_blank" rel="noreferrer">top-level browsing context</a>\u6765\u642D\u5EFA\u5929\u7136\u7684\u8DEF\u7531\u540C\u6B65\u673A\u5236</li><li>\u526F\u4F5C\u7528\u5C40\u9650\u5728\u6C99\u7BB1\u5185\u90E8\uFF0C\u5B50\u5E94\u7528\u5207\u6362\u65E0\u9700\u4EFB\u4F55\u6E05\u7406\u5DE5\u4F5C\uFF0C\u6CA1\u6709\u989D\u5916\u7684\u5207\u6362\u6210\u672C</li></ul></li><li><p><strong>\u6027\u80FD\u548C\u4F53\u79EF\u517C\u5177</strong></p><ul><li>\u5B50\u5E94\u7528\u6267\u884C\u6027\u80FD\u548C\u539F\u751F\u4E00\u81F4\uFF0C\u5B50\u5E94\u7528\u5B9E\u4F8B<code>instance</code>\u8FD0\u884C\u5728<code>iframe</code>\u7684<code>window</code>\u4E0A\u4E0B\u6587\u4E2D\uFF0C\u907F\u514D<code>with(proxyWindow){code}</code>\u8FD9\u6837\u6307\u5B9A\u4EE3\u7801\u6267\u884C\u4E0A\u4E0B\u6587\u5BFC\u81F4\u7684\u6027\u80FD\u4E0B\u964D\uFF0C\u4F46\u662F\u591A\u4E86\u5B9E\u4F8B\u5316<code>iframe</code>\u7684\u4E00\u6B21\u6027\u7684\u5F00\u9500\uFF0C\u53EF\u4EE5\u901A\u8FC7 <a href="/doc/api/preloadApp.html">proload</a> \u63D0\u524D\u5B9E\u4F8B\u5316</li><li>\u4F53\u79EF\u6BD4\u8F83\u8F7B\u91CF\uFF0C\u501F\u52A9<code>iframe</code>\u548C<code>webcomponent</code>\u6765\u5B9E\u73B0\u6C99\u7BB1\uFF0C\u6709\u6548\u7684\u51CF\u5C0F\u4E86\u4EE3\u7801\u91CF</li></ul></li><li><p><strong>\u5F00\u7BB1\u5373\u7528</strong></p><p>\u4E0D\u7BA1\u662F\u6837\u5F0F\u7684\u517C\u5BB9\u3001\u8DEF\u7531\u7684\u5904\u7406\u3001\u5F39\u7A97\u7684\u5904\u7406\u3001\u70ED\u66F4\u65B0\u7684\u52A0\u8F7D\uFF0C\u5B50\u5E94\u7528\u5B8C\u6210\u63A5\u5165\u5373\u53EF\u5F00\u7BB1\u5373\u7528\u65E0\u9700\u989D\u5916\u5904\u7406\uFF0C\u5E94\u7528<a href="./start.html#\u5B50\u5E94\u7528\u6539\u9020">\u63A5\u5165\u6210\u672C</a>\u4E5F\u6781\u4F4E</p></li></ul></div>',53),l=[t];function s(r,a,p,n,h,m){return o(),c("div",null,l)}const f=e(i,[["render",s]]);export{g as __pageData,f as default};
