import{_ as t,c as e,N as s,o as l}from"./chunks/framework.7db8a257.js";const u=JSON.parse('{"title":"创建一个新项目","description":"","frontmatter":{},"headers":[],"relativePath":"guide/install.md","lastUpdated":1683162364000}'),n={name:"guide/install.md"};function r(o,a,p,c,d,i){return l(),e("div",null,a[0]||(a[0]=[s(`<h1 id="创建一个新项目" tabindex="-1">创建一个新项目 <a class="header-anchor" href="#创建一个新项目" aria-label="Permalink to &quot;创建一个新项目&quot;">​</a></h1><h3 id="介绍" tabindex="-1">介绍 <a class="header-anchor" href="#介绍" aria-label="Permalink to &quot;介绍&quot;">​</a></h3><p><code>create-wujie</code> 用来帮助快速启动示例项目，方便开发者快速上手。</p><ul><li>支持选择一个或多个子应用来创建一个新的项目，便于针对特定框架进行开发测试。</li><li>支持主,子应用路由模式(<code>hash</code>, <code>history</code>)选择。</li></ul><p>后续会支持更多功能以及其他框架，版本的支持, 便于快速上手 <code>wujie</code>。</p><p>如在使用过程中遇到任何问题, 请在 <a href="https://github.com/wujie-micro/create-wujie/issues" target="_blank" rel="noreferrer">issues</a> 进行说明。</p><h3 id="快速开始" tabindex="-1">快速开始 <a class="header-anchor" href="#快速开始" aria-label="Permalink to &quot;快速开始&quot;">​</a></h3><p>开发环境配置:</p><ul><li><a href="https://nodejs.org/en/" target="_blank" rel="noreferrer">Node.js</a> 版本 &lt; 18.0.0</li><li><a href="https://pnpm.io/" target="_blank" rel="noreferrer">pnpm</a> 脚手架示例模版基于 pnpm + <a href="https://turborepo.org/docs/getting-started" target="_blank" rel="noreferrer">turborepo</a> 管理项目</li></ul><p>如果您的当前环境中需要切换 node.js 版本, 可以使用 <a href="https://github.com/nvm-sh/nvm" target="_blank" rel="noreferrer">nvm</a> or <a href="https://github.com/Schniz/fnm" target="_blank" rel="noreferrer">fnm</a> 进行安装.</p><p>以下是通过 nvm 安装 Node.js 16 LTS 版本的示例:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;"># Install the LTS version of Node.js 16</span></span>
<span class="line"><span style="color:#FFCB6B;">nvm</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">install</span><span style="color:#BABED8;"> </span><span style="color:#F78C6C;">16</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">--lts</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># Make the newly installed Node.js 16 as the default version</span></span>
<span class="line"><span style="color:#FFCB6B;">nvm</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">alias</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">default</span><span style="color:#BABED8;"> </span><span style="color:#F78C6C;">16</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># Switch to the newly installed Node.js 16</span></span>
<span class="line"><span style="color:#FFCB6B;">nvm</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">use</span><span style="color:#BABED8;"> </span><span style="color:#F78C6C;">16</span></span>
<span class="line"></span></code></pre></div><h3 id="安装" tabindex="-1">安装 <a class="header-anchor" href="#安装" aria-label="Permalink to &quot;安装&quot;">​</a></h3><p>打开一个终端并使用以下命令创建一个新的<code>wujie demo</code> 示例：</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;"># Create a new wujie-app project</span></span>
<span class="line"><span style="color:#FFCB6B;">npx</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">create-wujie@latest</span></span>
<span class="line"></span></code></pre></div><h3 id="模版列表" tabindex="-1">模版列表 <a class="header-anchor" href="#模版列表" aria-label="Permalink to &quot;模版列表&quot;">​</a></h3><ul><li>主应用列表</li></ul><table><thead><tr><th>主应用框架</th><th></th></tr></thead><tbody><tr><td>Webpack + Vue2</td><td>✅</td></tr><tr><td>Webpack + React17</td><td>✅</td></tr><tr><td>Vite + Vue3</td><td>🚧</td></tr></tbody></table><ul><li>子应用列表</li></ul><table><thead><tr><th>子应用框架</th><th></th></tr></thead><tbody><tr><td>Vite + Vue3</td><td>✅</td></tr><tr><td>Webpack + Vue2</td><td>✅</td></tr><tr><td>Webpack + Vue3</td><td>✅</td></tr><tr><td>Webpack + React16</td><td>✅</td></tr><tr><td>Webpack + React17</td><td>✅</td></tr><tr><td>Angular12</td><td>✅</td></tr></tbody></table>`,20)]))}const b=t(n,[["render",r]]);export{u as __pageData,b as default};
